(function(){Application.controller("AddNewManualController",["$scope","$http",function(n,t){n.Categories=[];n.NewManual={Tags:[]};n.Tags=[];t({method:"GET",url:"/Home/GetCategories"}).success(function(t){n.Categories=t;n.NewManual.CategoryId=n.Categories[0].Id});t({method:"GET",url:"/Home/GetTags",data:{Id:n.NewManual.Id}}).success(function(t){n.Tags=t});n.AddTag=function(){return t({method:"POST",url:"/Home/AddNewTag",data:{tagName:n.NewTag}}).success(function(t){t.success&&n.NewManual.Tags.push(t.tag);n.NewTag=""})};n.Create=function(i){return t({method:"POST",url:"/Home/CreateNewManual",data:{model:i,ReturnUrl:n.ReturnUrl}}).success(function(n){n.result==="Redirect"&&(window.location.href=n.url)})}}])}).call(this);