(function(){Application.controller("AddNewCreativeController",["$scope","$http",function(n,t){n.Categories=[];n.NewCreative={Tags:[]};n.Tags=[];t({method:"GET",url:"/Home/GetCategories"}).success(function(t){n.Categories=t});t({method:"GET",url:"/Home/GetTags",data:{Id:n.NewCreative.Id}}).success(function(t){n.Tags=t});n.AddTag=function(){return t({method:"POST",url:"/Home/AddNewTag",data:{tagName:n.NewTag}}).success(function(t){t.success&&n.NewCreative.Tags.push(t.tag);n.NewTag=""})};n.Create=function(n){return t({method:"POST",url:"/Home/CreateNewCreative",data:n})}}])}).call(this);